import java.util.*; public class Vowel_Remove {     public static void main(String args[])     {         Scanner sc=new Scanner(System.in);         System.out.println("Enter any Sentence --");         String s=sc.nextLine();         String m="";         for(int i=0;i<s.length();i++)         {             char c=s.charAt(i);             c=Character.toLowerCase(c);             if(c=='a' || c=='e' || c=='i' || c=='o' || c=='u' )               continue;             m+=Character.toString(s.charAt(i));           }         System.out.println("Sentence -- "+m);     } }